// Extract and transform Clay data for Instantly
const clayData = $input.first().json;

// Map Clay fields to Instantly format
const instantlyLead = {
  email: clayData.email || clayData.Email,
  first_name: clayData.firstName || clayData['First Name'] || clayData.first_name,
  last_name: clayData.lastName || clayData['Last Name'] || clayData.last_name,
  company_name: clayData.companyName || clayData['Company Name'] || clayData.company,
  personalization_line1: clayData.personalizationLine1 || clayData.commentText || '',
  personalization_line2: clayData.personalizationLine2 || clayData.engagementType || '',
  // Add custom variables for Instantly
  custom_linkedin_url: clayData.linkedinUrl || clayData['LinkedIn URL'],
  custom_title: clayData.title || clayData.Title,
  custom_lead_score: clayData.leadScore || clayData['Lead Score'] || '',
  custom_engagement_type: clayData.engagementType || '',
  // Tags for segmentation
  tags: [
    clayData.source || 'linkedin',
    clayData.engagementType || 'comment',
    `score_${clayData.leadScore || 'unknown'}`
  ].filter(Boolean).join(',')
};

// Only proceed if we have an email
if (!instantlyLead.email) {
  throw new Error('No email found in Clay data');
}

return [{ json: instantlyLead }];
